mapper.select:
select member_id, member_name, token
from member
where member_id = #{memberId} and member_password = #{memberPassword}

mapper.update:
update member
set token = #{token}
where member_id = #{memberId}

mapper.select:
select token
from member
where member_id = #{memberId}

mapper.update:
update member
set token = #{token, jdbcType=VARCHAR}
where member_id = #{memberId}

mapper.select:
select count(*)
from member
where member_id = #{memberId}

select *
from member
where member_id = #{memberId} and member_password = #{memberPassword}
