mapper.select:
select *
from review
where hotplace_id = #{hotplaceId} and member_id = #{memberId}

mapper.update:
update review
set score = #{score},
    comment = #{comment}	
where hotplace_id = #{hotplaceId} and member_id = #{memberId}

mapper.delete:
delete 
from review
where hotplace_id = #{hotplaceId} and member_id = #{memberId}

mapper.update:
update review
set helpful_count = helpful_count+1
where hotplace_id = #{hotplaceId} and member_id = #{memberId}
